angular.module('sequelize', ['io']);

angular.module('sequelize')
    .service('$sequelize', [
        '$q', '$http', '$rootScope', '$io',
        function ($q, $http, $rootScope, $io) {
            var service = new sequelize.Service({}, $rootScope, $q, $http, $io.raw );
            service.sync();
            return service;
        }]);